<!-- Trainer Modal -->\n<div class="modal fade" id="trainerModal" tabindex="-1">\n    <div class="modal-dialog modal-xl">\n        <div class="modal-content">\n            <div class="modal-header" style="background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%); color: white; padding: 0.5rem 1rem;">\n                <h6 class="modal-title mb-0" id="trainerModalLabel"><i class="fas fa-user-plus me-2"></i>Add New Trainer</h6>\n                <button type="button" class="btn-close btn-close-white" data-bs-dismiss="modal" style="padding: 0.25rem;"></button>\n            </div>\n            <div class="modal-body" style="max-height: 75vh; overflow-y: auto; padding: 0.75rem;">\n                <form id="trainerForm" enctype="multipart/form-data">\n                    <input type="hidden" id="trainerId" name="id">\n                    \n                    <!-- Personal Information -->\n                    <div class="card mb-2">\n                        <div class="card-header bg-primary text-white py-1">\n                            <small class="mb-0 fw-bold"><i class="fas fa-user me-1"></i>Personal Information</small>\n                        </div>\n                        <div class="card-body p-2">\n                            <div class="row g-2">\n                                <div class="col-md-4">\n                                    <label for="trainerFirstName" class="form-label mb-1 small">First Name *</label>\n                                    <input type="text" class="form-control form-control-sm" id="trainerFirstName" name="first_name" required>\n                                </div>\n                                <div class="col-md-4">\n                                    <label for="trainerMiddleName" class="form-label mb-1 small">Middle Name</label>\n                                    <input type="text" class="form-control form-control-sm" id="trainerMiddleName" name="middle_name">\n                                </div>\n                                <div class="col-md-4">\n                                    <label for="trainerLastName" class="form-label mb-1 small">Last Name *</label>\n                                    <input type="text" class="form-control form-control-sm" id="trainerLastName" name="last_name" required>\n                                </div>\n                                <div class="col-md-3">\n                                    <label for="trainerGender" class="form-label mb-1 small">Gender *</label>\n                                    <select class="form-select form-select-sm" id="trainerGender" name="gender" required>\n                                        <option value="">Select</option>\n                                        <option value="Male">Male</option>\n                                        <option value="Female">Female</option>\n                                        <option value="Other">Other</option>\n                                    </select>\n                                </div>\n                                <div class="col-md-3">\n                                    <label for="trainerDOB" class="form-label mb-1 small">Date of Birth</label>\n                                    <input type="date" class="form-control form-control-sm" id="trainerDOB" name="date_of_birth">\n                                </div>\n                                <div class="col-md-3">\n                                    <label for="trainerNationality" class="form-label mb-1 small">Nationality</label>\n                                    <input type="text" class="form-control form-control-sm" id="trainerNationality" name="nationality">\n                                </div>\n                                <div class="col-md-3">\n                                    <label for="trainerIDNumber" class="form-label mb-1 small">ID Number</label>\n                                    <input type="text" class="form-control form-control-sm" id="trainerIDNumber" name="id_number">\n                                </div>\n                            </div>\n                        </div>\n                    </div>\n\n                    <!-- Contact Information -->\n                    <div class="card mb-2">\n                        <div class="card-header bg-success text-white py-1">\n                            <small class="mb-0 fw-bold"><i class="fas fa-phone me-1"></i>Contact Information</small>\n                        </div>\n                        <div class="card-body p-2">\n                            <div class="row g-2">\n                                <div class="col-md-4">\n                                    <label for="trainerEmail" class="form-label mb-1 small">Email Address *</label>\n                                    <input type="email" class="form-control form-control-sm" id="trainerEmail" name="email" required>\n                                </div>\n                                <div class="col-md-4">\n                                    <label for="trainerPhone" class="form-label mb-1 small">Phone Number *</label>\n                                    <input type="tel" class="form-control form-control-sm" id="trainerPhone" name="phone" required>\n                                </div>\n                                <div class="col-md-4">\n                                    <label for="trainerWhatsapp" class="form-label mb-1 small">WhatsApp Number</label>\n                                    <input type="tel" class="form-control form-control-sm" id="trainerWhatsapp" name="whatsapp">\n                                </div>\n                                <div class="col-md-6">\n                                    <label for="trainerEmergencyContact" class="form-label mb-1 small">Emergency Contact Name</label>\n                                    <input type="text" class="form-control form-control-sm" id="trainerEmergencyContact" name="emergency_contact">\n                                </div>\n                                <div class="col-md-6">\n                                    <label for="trainerEmergencyPhone" class="form-label mb-1 small">Emergency Contact Phone</label>\n                                    <input type="tel" class="form-control form-control-sm" id="trainerEmergencyPhone" name="emergency_phone">\n                                </div>\n                            </div>\n                        </div>\n                    </div>\n\n                    <!-- Address Information -->\n                    <div class="card mb-2">\n                        <div class="card-header bg-info text-white py-1">\n                            <small class="mb-0 fw-bold"><i class="fas fa-map-marker-alt me-1"></i>Address Information</small>\n                        </div>\n                        <div class="card-body p-2">\n                            <div class="row g-2">\n                                <div class="col-md-3">\n                                    <label for="trainerCountry" class="form-label mb-1 small">Country *</label>\n                                    <select class="form-select form-select-sm" id="trainerCountry" name="country" required>\n                                        <option value="Uganda" selected>Uganda</option>\n                                        <option value="Kenya">Kenya</option>\n                                        <option value="Tanzania">Tanzania</option>\n                                        <option value="Rwanda">Rwanda</option>\n                                    </select>\n                                </div>\n                                <div class="col-md-3">\n                                    <label for="trainerRegion" class="form-label mb-1 small">Region *</label>\n                                    <select class="form-select form-select-sm" id="trainerRegion" name="region" required>\n                                        <option value="">Select</option>\n                                        <option value="Central">Central</option>\n                                        <option value="Eastern">Eastern</option>\n                                        <option value="Northern">Northern</option>\n                                        <option value="Western">Western</option>\n                                        <option value="Kampala">Kampala</option>\n                                    </select>\n                                </div>\n                                <div class="col-md-3">\n                                    <label for="trainerDistrict" class="form-label mb-1 small">District *</label>\n                                    <input type="text" class="form-control form-control-sm" id="trainerDistrict" name="district" required>\n                                </div>\n                                <div class="col-md-3">\n                                    <label for="trainerSubCounty" class="form-label mb-1 small">Sub-County</label>\n                                    <input type="text" class="form-control form-control-sm" id="trainerSubCounty" name="sub_county">\n                                </div>\n                                <div class="col-md-6">\n                                    <label for="trainerVillage" class="form-label mb-1 small">Village/Street</label>\n                                    <input type="text" class="form-control form-control-sm" id="trainerVillage" name="village">\n                                </div>\n                                <div class="col-md-6">\n                                    <label for="trainerPostalCode" class="form-label mb-1 small">Postal Code</label>\n                                    <input type="text" class="form-control form-control-sm" id="trainerPostalCode" name="postal_code">\n                                </div>\n                            </div>\n                        </div>\n                    </div>\n\n                    <!-- Professional Information -->\n                    <div class="card mb-2">\n                        <div class="card-header bg-warning text-dark py-1">\n                            <small class="mb-0 fw-bold"><i class="fas fa-briefcase me-1"></i>Professional Information</small>\n                        </div>\n                        <div class="card-body p-2">\n                            <div class="row g-2">\n                                <div class="col-md-4">\n                                    <label for="trainerSpecialty" class="form-label mb-1 small">Specialty *</label>\n                                    <select class="form-select form-select-sm" id="trainerSpecialty" name="specialty" required>\n                                        <option value="">Select</option>\n                                        <option value="E-Cooking Training">E-Cooking Training</option>\n                                        <option value="Safety Training">Safety Training</option>\n                                        <option value="Technical Training">Technical Training</option>\n                                        <option value="Maintenance Training">Maintenance Training</option>\n                                        <option value="General Appliance Training">General Appliance Training</option>\n                                    </select>\n                                </div>\n                                <div class="col-md-4">\n                                    <label for="trainerExperience" class="form-label mb-1 small">Experience (Years) *</label>\n                                    <input type="number" class="form-control form-control-sm" id="trainerExperience" name="experience" min="0" max="50" required>\n                                </div>\n                                <div class="col-md-4">\n                                    <label for="trainerLicenseNumber" class="form-label mb-1 small">License/Certification Number</label>\n                                    <input type="text" class="form-control form-control-sm" id="trainerLicenseNumber" name="license_number">\n                                </div>\n                                <div class="col-md-4">\n                                    <label for="trainerHourlyRate" class="form-label mb-1 small">Hourly Rate (UGX)</label>\n                                    <input type="number" class="form-control form-control-sm" id="trainerHourlyRate" name="hourly_rate" min="0" step="1000">\n                                </div>\n                                <div class="col-md-4">\n                                    <label for="trainerDailyRate" class="form-label mb-1 small">Daily Rate (UGX)</label>\n                                    <input type="number" class="form-control form-control-sm" id="trainerDailyRate" name="daily_rate" min="0" step="1000">\n                                </div>\n                                <div class="col-md-4">\n                                    <label for="trainerStatus" class="form-label mb-1 small">Status *</label>\n                                    <select class="form-select form-select-sm" id="trainerStatus" name="status" required>\n                                        <option value="Active">Active</option>\n                                        <option value="Inactive">Inactive</option>\n                                        <option value="On Leave">On Leave</option>\n                                        <option value="Suspended">Suspended</option>\n                                    </select>\n                                </div>\n                            </div>\n                        </div>\n                    </div>\n\n                    <!-- Skills & Qualifications -->\n                    <div class="card mb-2">\n                        <div class="card-header bg-secondary text-white py-1">\n                            <small class="mb-0 fw-bold"><i class="fas fa-graduation-cap me-1"></i>Skills & Qualifications</small>\n                        </div>\n                        <div class="card-body p-2">\n                            <div class="row g-2">\n                                <div class="col-md-6">\n                                    <label for="trainerSkills" class="form-label mb-1 small">Skills (comma separated)</label>\n                                    <textarea class="form-control form-control-sm" id="trainerSkills" name="skills" rows="2" placeholder="e.g., E-cooking techniques, Safety protocols"></textarea>\n                                </div>\n                                <div class="col-md-6">\n                                    <label for="trainerQualifications" class="form-label mb-1 small">Educational Qualifications</label>\n                                    <textarea class="form-control form-control-sm" id="trainerQualifications" name="qualifications" rows="2" placeholder="e.g., Bachelor's in Engineering"></textarea>\n                                </div>\n                                <div class="col-md-6">\n                                    <label for="trainerCertifications" class="form-label mb-1 small">Certifications</label>\n                                    <textarea class="form-control form-control-sm" id="trainerCertifications" name="certifications" rows="2" placeholder="e.g., Certified Trainer"></textarea>\n                                </div>\n                                <div class="col-md-6">\n                                    <label for="trainerLanguages" class="form-label mb-1 small">Languages Spoken</label>\n                                    <textarea class="form-control form-control-sm" id="trainerLanguages" name="languages" rows="2" placeholder="e.g., English, Luganda"></textarea>\n                                </div>\n                            </div>\n                        </div>\n                    </div>\n\n                    <!-- Training Statistics -->\n                    <div class="card mb-2">\n                        <div class="card-header bg-dark text-white py-1">\n                            <small class="mb-0 fw-bold"><i class="fas fa-chart-bar me-1"></i>Training Statistics</small>\n                        </div>\n                        <div class="card-body p-2">\n                            <div class="row g-2">\n                                <div class="col-md-3">\n                                    <label for="trainerSessionsCount" class="form-label mb-1 small">Total Sessions Conducted</label>\n                                    <input type="number" class="form-control form-control-sm" id="trainerSessionsCount" name="sessions_count" min="0">\n                                </div>\n                                <div class="col-md-3">\n                                    <label for="trainerStudentsCount" class="form-label mb-1 small">Total Students Trained</label>\n                                    <input type="number" class="form-control form-control-sm" id="trainerStudentsCount" name="students_count" min="0">\n                                </div>\n                                <div class="col-md-3">\n                                    <label for="trainerRating" class="form-label mb-1 small">Average Rating (1-5)</label>\n                                    <input type="number" class="form-control form-control-sm" id="trainerRating" name="rating" min="1" max="5" step="0.1">\n                                </div>\n                                <div class="col-md-3">\n                                    <label for="trainerAvailability" class="form-label mb-1 small">Weekly Availability (hours)</label>\n                                    <input type="number" class="form-control form-control-sm" id="trainerAvailability" name="weekly_hours" min="0" max="168">\n                                </div>\n                            </div>\n                        </div>\n                    </div>\n\n                    <!-- Profile Picture & Notes -->\n                    <div class="card mb-2">\n                        <div class="card-header bg-light text-dark py-1">\n                            <small class="mb-0 fw-bold"><i class="fas fa-image me-1"></i>Profile Picture & Additional Notes</small>\n                        </div>\n                        <div class="card-body p-2">\n                            <div class="row g-2">\n                                <div class="col-md-6">\n                                    <label for="trainerImage" class="form-label mb-1 small">Profile Picture</label>\n                                    <input type="file" class="form-control form-control-sm" id="trainerImage" name="image" accept="image/*">\n                                    <div id="currentTrainerImage" class="mt-1"></div>\n                                </div>\n                                <div class="col-md-6">\n                                    <label for="trainerNotes" class="form-label mb-1 small">Additional Notes</label>\n                                    <textarea class="form-control form-control-sm" id="trainerNotes" name="notes" rows="3" placeholder="Additional information..."></textarea>\n                                </div>\n                            </div>\n                        </div>\n                    </div>\n\n                    <!-- Account Information -->\n                    <div class="card mb-2">\n                        <div class="card-header bg-danger text-white py-1">\n                            <small class="mb-0 fw-bold"><i class="fas fa-key me-1"></i>Account Information</small>\n                        </div>\n                        <div class="card-body p-2">\n                            <div class="row g-2">\n                                <div class="col-md-6">\n                                    <label for="trainerPassword" class="form-label mb-1 small">Password <small class="text-muted">(leave blank to keep current)</small></label>\n                                    <input type="password" class="form-control form-control-sm" id="trainerPassword" name="password" minlength="6">\n                                </div>\n                                <div class="col-md-6">\n                                    <label for="trainerPasswordConfirm" class="form-label mb-1 small">Confirm Password</label>\n                                    <input type="password" class="form-control form-control-sm" id="trainerPasswordConfirm" name="password_confirmation" minlength="6">\n                                </div>\n                            </div>\n                        </div>\n                    </div>\n                </form>\n            </div>\n            <div class="modal-footer py-2">\n                <button type="button" class="btn btn-sm btn-secondary" data-bs-dismiss="modal">\n                    <i class="fas fa-times me-1"></i>Cancel\n                </button>\n                <button type="button" class="btn btn-sm btn-outline-danger" onclick="resetTrainerForm()">\n                    <i class="fas fa-undo me-1"></i>Reset\n                </button>\n                <button type="submit" form="trainerForm" class="btn btn-sm btn-danger">\n                    <i class="fas fa-save me-1"></i>Save\n                </button>\n            </div>\n        </div>\n    </div>\n</div>
